import React from 'react';
import ReactDOM from 'react-dom';
import * as Rx from 'typeless/rx';
import { createModule, DefaultTypelessProvider } from 'typeless';
import QiitaRepository from './repository/Qiita'
import Item from './models/Item'
import {Tags} from './models/Tag'

/* == Module Interface == */

export const [useModule, CounterActions, getCounterState] = createModule(
  Symbol('counter')
)
  // Create Actions Creators
  .withActions({
    $mounted: null,
    getItems: (tags: string) => ({ payload: { tags } }), // null means no args
    getItemsDone: (items: Array<Item>) => ({ payload: { items } }),
    getTags: null, // null means no args
    getTagsDone: (tags: any) => ({ payload: { tags } }),
  })
  .withState<CounterState>();

export interface CounterState {
  items: Array<Item>,
  tags?: Tags
}

/* == Module Implementation == */

const initialState: CounterState = {
  items: [],
  tags: undefined
};

const repository = new QiitaRepository()

// Create Epic for side effects
useModule
  .epic()
  // Listen for `count` and dispatch `countDone` with 500ms delay
  .on(CounterActions.$mounted, ()  => {return Rx.of(CounterActions.getTags())})
  .on(CounterActions.getTags, async() => {
    const page = Math.floor( Math.random() * 99 ) + 1
    return repository.getTags(page).then((result) => CounterActions.getTagsDone(result))
  })
  .on(CounterActions.getTagsDone, ({tags}) => {return Rx.of(CounterActions.getItems(tags))})
  .on(CounterActions.getItems, async({tags}) => {
    console.log(tags)
    return repository.getItems(tags).then((result) => CounterActions.getItemsDone(result))
  });

// Create a reducer
// Under the hood it uses `immer` and state mutations are allowed
useModule
  .reducer(initialState)
  .on(CounterActions.getTagsDone, (state, {tags}) => {
    state.tags = tags
  })
  .on(CounterActions.getItemsDone, (state, { items }) => {
    state.items = items;
  });

/* == Use Module in React == */

export function Counter() {
  // load epic and reducer
  useModule();

  // wrap actions with `dispatch`
  // get state from store
  const { items } = getCounterState.useState();

  return (
    <div>
      {items.map((x, index) => <div key={index}>{x.title}</div>)}
    </div>
  );
}

ReactDOM.render(
  <DefaultTypelessProvider>
    <Counter />
  </DefaultTypelessProvider>,
  document.getElementById('app')
);
