import React from 'react';
import ReactDOM from 'react-dom';
import * as Rx from 'typeless/rx';
import { createModule, useActions, DefaultTypelessProvider } from 'typeless';
import QiitaRepository from './repository/Qiita'
import Item from './models/Item'

/* == Module Interface == */

export const [useModule, CounterActions, getCounterState] = createModule(
  Symbol('counter')
)
  // Create Actions Creators
  .withActions({
    $mount: null,
    startCount: null, // null means no args
    countDone: (items: any) => ({ payload: { items } }),
  })
  .withState<CounterState>();

export interface CounterState {
  isLoading: boolean;
  count: number;
  items: Array<Item>
}

/* == Module Implementation == */

const initialState: CounterState = {
  isLoading: false,
  count: 0,
  items: []
};

// Create Epic for side effects
useModule
  .epic()
  // Listen for `count` and dispatch `countDone` with 500ms delay
  .on(CounterActions.$mount, () => {return Rx.from(CounterActions.startCount)})
  .on(CounterActions.startCount, async() => {
    const repository = new QiitaRepository()
    return repository.getItem().then((response) => CounterActions.countDone(response))
  });

// Create a reducer
// Under the hood it uses `immer` and state mutations are allowed
useModule
  .reducer(initialState)
  .on(CounterActions.startCount, state => {
    state.isLoading = true;
  })
  .on(CounterActions.countDone, (state, { items }) => {
    state.isLoading = false;
    state.items = items;
  });

/* == Use Module in React == */

export function Counter() {
  // load epic and reducer
  useModule();

  // wrap actions with `dispatch`
  const { startCount } = useActions(CounterActions);
  // get state from store
  const { items } = getCounterState.useState();

  return (
    <div>
      {items.map((x, index) => <div key={index}>{x.title}</div>)}
    </div>
  );
}

ReactDOM.render(
  <DefaultTypelessProvider>
    <Counter />
  </DefaultTypelessProvider>,
  document.getElementById('app')
);
